import { assert } from "chai";
import { parseHeaderParams } from "../lib/utils";

describe("parseHeaderParams", function () {
  interface Item {
    name: string;
    header: string;
    expected: Map<string, string>;
  }

  const items: Item[] = [
    {
      name: "1",
      // tslint:disable-next-line:max-line-length
      header: "; expires=1463340242; endpointId={bf8fd849-4844-437d-9919-1f1744d17f7d}",
      expected: new Map([
        // tslint:disable-next-line:max-line-length
        ["registrationToken", ""],
        ["expires", "1463340242"],
        ["endpointId", "{bf8fd849-4844-437d-9919-1f1744d17f7d}"],
      ]),
    },
  ];

  for (const item of items) {
    it(`should parse the known header ${item.name}`, function () {
      const actual: Map<string, string> = parseHeaderParams(item.header);
      assert.deepEqual(actual, item.expected);
    });
  }
});
